import React, { useEffect, useState} from 'react';
import './Dashboard.css';
import axios from 'axios';
import Header from './components/header';
import ProductList from './components/ProductList';

export const Dashboard = () => {
/* 
    const [user, setUser] = useState({});

    useEffect(() => {
        getUser()
    }, []);

    const getUser = () => {
        const user = JSON.parse(localStorage.user);
        setUser(user);
    } */

    const [shoes, setShoes] = useState([])
    
    useEffect(()=>{
        getShoes()
    }, [])
    
    const getShoes = async() => {
        try {
            const {data} = await axios.get("http://localhost:4000/product/showAll")
            setShoes(data.shoes)
            
        } catch (error) {
            console.log("Error al obtener los tenis", error)
        }
    }

    return (
    <>
      <Header></Header>
        <main>
            <div className="container">
            <div className="container-main">
                <h1 className="text-center">¡Bienvenido!</h1>
                <p>¡Gracias por ser parte de Kingdom Shoes!</p>

                <ProductList></ProductList>
            </div>
            </div>
    </main>
        
    <footer style="background-color: #222; color: white; padding: 30px 0; text-align: center;">
    <div class="container">
        <a href="#" id="logo-white" style="font-size: 1.8rem; font-weight: bold; color: gold; text-decoration: none;">
            Kingdom Shoes 👟
        </a>

        <nav class="menu-nav-footer" style="margin: 15px 0;">
            <ul style="list-style: none; padding: 0; display: flex; justify-content: center; gap: 20px;">
                <li>
                    <a href="#" style="color: white; text-decoration: none; font-size: 1rem; transition: 0.3s;">
                        Fuentes
                    </a>
                </li>
                <li>
                    <a href="#" style="color: white; text-decoration: none; font-size: 1rem; transition: 0.3s;">
                        Artículos
                    </a>
                </li>
                <li>
                    <a href="#" style="color: white; text-decoration: none; font-size: 1rem; transition: 0.3s;">
                        Conoce +
                    </a>
                </li>
            </ul>
        </nav>

        <div class="social-icons" style="margin: 20px 0;">
            <a href="#" style="margin: 0 10px; color: white; font-size: 1.5rem; transition: 0.3s;">
                <i class="fab fa-facebook"></i>
            </a>
            <a href="#" style="margin: 0 10px; color: white; font-size: 1.5rem; transition: 0.3s;">
                <i class="fab fa-instagram"></i>
            </a>
            <a href="#" style="margin: 0 10px; color: white; font-size: 1.5rem; transition: 0.3s;">
                <i class="fab fa-twitter"></i>
            </a>
        </div>

        <p style="font-size: 0.9rem; margin-top: 10px; opacity: 0.7;">
            &copy; Kingdom Shoes 2024. We love you! 💙
        </p>
    </div>
</footer>

    </>
    )};
